import img from './pup.jpg';
import './index.css';
import {useState} from 'react';

function Image() {
  return (
    <div classNa="img">
      <h1>my-spa-project</h1>
        <img src={img} class="img-pup"alt="mpuppy" />

       </div>
  );
}

function ButtonWithState(){
  const [counter, setCounter] = useState(0)

  function handleClick(e){
    e.preventDefault();
    setCounter(counter+1);
    alert(`The button was clicked ${counter} times`);
  }

return (
  <>
  <button onClick={handleClick}>Click Here(WithState)</button>
 <p>With state:{counter}</p>
  </>
);
}
export default Image;
